// src/components/AddTaskModal.js
import React, { useState } from "react";
import { XMarkIcon } from "@heroicons/react/24/outline";

export default function AddTaskModal({ isOpen, onClose, onAdd }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [priority, setPriority] = useState("Low");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title.trim()) return;
    onAdd({ title, description, priority });
    setTitle("");
    setDescription("");
    setPriority("Low");
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
      <div className="bg-white rounded-2xl shadow-xl w-full max-w-lg p-6 relative">
        {/* Close button */}
        <button
          onClick={onClose}
          className="absolute top-3 right-3 p-2 rounded-full hover:bg-gray-100"
        >
          <XMarkIcon className="w-5 h-5 text-gray-500" />
        </button>

        {/* Header */}
        <h2 className="text-lg font-semibold mb-4 text-gray-800">Add New Task</h2>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Title */}
          <input
            type="text"
            placeholder="Task title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full border rounded-full px-4 py-2 focus:ring-2 focus:ring-purple-500 outline-none"
          />

          {/* Description */}
          <textarea
            placeholder="Task description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full border rounded-2xl px-4 py-2 focus:ring-2 focus:ring-purple-500 outline-none"
            rows="3"
          />

          {/* Priority dropdown */}
          <select
            value={priority}
            onChange={(e) => setPriority(e.target.value)}
            className="w-full border rounded-full px-4 py-2 focus:ring-2 focus:ring-purple-500 outline-none"
          >
            <option value="Low">Low Priority</option>
            <option value="High">High Priority</option>
            <option value="Completed">Completed</option>
          </select>

          {/* Submit */}
          <button
            type="submit"
            className="w-full bg-purple-600 text-white py-2 rounded-full hover:bg-purple-700 transition"
          >
            Add Task
          </button>
        </form>
      </div>
    </div>
  );
}
