// src/components/FilterBar.js
import React, { useState } from "react";
import AddTaskModal from "./AddTaskModal";
import { FunnelIcon, PlusIcon, MagnifyingGlassIcon } from "@heroicons/react/24/outline";

export default function FilterBar({ onAddTask }) {
  const [isModalOpen, setIsModalOpen] = useState(false);

  return (
    <div className="flex items-center justify-between bg-white rounded-2xl shadow-sm p-4 mb-6">
      {/* Left side: search & filter */}
      <div className="flex items-center gap-3 w-full max-w-md">
        <div className="relative flex-1">
          <MagnifyingGlassIcon className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
          <input
            type="text"
            placeholder="Search tasks..."
            className="w-full border rounded-full pl-10 pr-4 py-2 focus:ring-2 focus:ring-purple-500 outline-none"
          />
        </div>
        <button className="flex items-center gap-2 px-4 py-2 rounded-full border hover:bg-gray-50">
          <FunnelIcon className="h-5 w-5 text-gray-500" />
          <span className="text-sm text-gray-600">Filter</span>
        </button>
      </div>

      {/* Right side: Add Task */}
      <div>
        <button
          onClick={() => setIsModalOpen(true)}
          className="flex items-center gap-2 bg-purple-600 text-white px-4 py-2 rounded-full hover:bg-purple-700 transition"
        >
          <PlusIcon className="h-5 w-5" />
          <span className="text-sm font-medium">Add Task</span>
        </button>
      </div>

      {/* Modal */}
      <AddTaskModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onAdd={onAddTask}
      />
    </div>
  );
}
