// src/components/Sidebar.jsx
import React, { useState } from "react";
import { useLocation, Link } from "react-router-dom"; // optional - works if you use react-router
// IMPORTANT: import from Heroicons v2 path
import {
  HomeIcon,
  ClipboardDocumentListIcon,
  CalendarDaysIcon,
  ChatBubbleLeftEllipsisIcon,
  Cog6ToothIcon,
} from "@heroicons/react/24/outline";

const menuItems = [
  { label: "Home", to: "/", Icon: HomeIcon },
  { label: "My Tasks", to: "/tasks", Icon: ClipboardDocumentListIcon },
  { label: "Calendar", to: "/calendar", Icon: CalendarDaysIcon },
  { label: "Messages", to: "/messages", Icon: ChatBubbleLeftEllipsisIcon },
  { label: "Settings", to: "/settings", Icon: Cog6ToothIcon },
];

export default function Sidebar({ initialActive } = {}) {
  const location = (() => {
    try {
      // If react-router is not used this will still safely fail
      return useLocation();
    } catch (e) {
      return null;
    }
  })();

  // fallback local active state (works when no router is present)
  const [localActive, setLocalActive] = useState(initialActive || "Home");

  const getActive = (item) => {
    if (location && location.pathname) {
      // compare route paths
      return item.to === (location.pathname === "/" ? "/" : location.pathname);
    }
    return item.label === localActive;
  };

  return (
    <aside className="w-64 h-screen bg-white border-r flex flex-col">
      {/* Logo */}
      <div className="h-16 flex items-center px-6 border-b">
        <span className="text-lg font-bold text-purple-600">Creative Upaay</span>
      </div>

      {/* Menu */}
      <nav className="flex-1 px-2 py-6 space-y-1">
        {menuItems.map(({ label, Icon, to }) => {
          const active = getActive({ label, to });
          return (
            <div key={label}>
              {/* Use Link if react-router exists, otherwise a button */}
              {location ? (
                <Link
                  to={to}
                  className={`flex items-center gap-3 px-3 py-2 rounded-lg text-sm transition
                    ${active ? "bg-purple-50 text-purple-600 font-semibold" : "text-gray-600 hover:bg-gray-50"}
                  `}
                  aria-current={active ? "page" : undefined}
                >
                  <Icon className={`w-5 h-5 ${active ? "text-purple-600" : "text-gray-400"}`} />
                  <span>{label}</span>
                </Link>
              ) : (
                <button
                  onClick={() => setLocalActive(label)}
                  className={`w-full flex items-center gap-3 px-3 py-2 rounded-lg text-sm text-left transition
                    ${active ? "bg-purple-50 text-purple-600 font-semibold" : "text-gray-600 hover:bg-gray-50"}
                  `}
                  aria-pressed={active}
                >
                  <Icon className={`w-5 h-5 ${active ? "text-purple-600" : "text-gray-400"}`} />
                  <span>{label}</span>
                </button>
              )}
            </div>
          );
        })}
      </nav>

      {/* Profile Section */}
      <div className="p-4 border-t flex items-center gap-3">
        <img src="/avatars/a1.png" alt="Profile" className="w-10 h-10 rounded-full" />
        <div>
          <p className="text-sm font-semibold text-gray-900">Jai Indra</p>
          <p className="text-xs text-gray-500">Frontend Developer</p>
        </div>
      </div>
    </aside>
  );
}
