import React from "react";
import {
  HomeIcon,
  ClipboardListIcon,
  CalendarIcon,
  ChatAltIcon,
  CogIcon,
} from "@heroicons/react/outline";

const menuItems = [
  { label: "Home", icon: HomeIcon },
  { label: "My Tasks", icon: ClipboardListIcon },
  { label: "Calendar", icon: CalendarIcon },
  { label: "Messages", icon: ChatAltIcon },
  { label: "Settings", icon: CogIcon },
];

export default function Sidebar() {
  return (
    <aside className="w-64 h-screen bg-white border-r flex flex-col">
      {/* Logo */}
      <div className="h-16 flex items-center px-6 border-b">
        <span className="text-xl font-bold text-purple-600">Creative Upaay</span>
      </div>

      {/* Menu */}
      <nav className="flex-1 px-4 py-6 space-y-2">
        {menuItems.map(({ label, icon: Icon }) => (
          <button
            key={label}
            className="w-full flex items-center px-3 py-2 text-gray-600 rounded-lg hover:bg-purple-50 hover:text-purple-600"
          >
            <Icon className="w-5 h-5 mr-3" />
            <span className="text-sm font-medium">{label}</span>
          </button>
        ))}
      </nav>

      {/* Profile Section */}
      <div className="p-4 border-t flex items-center space-x-3">
        <img
          src="/avatars/a1.png"
          alt="Profile"
          className="w-10 h-10 rounded-full"
        />
        <div>
          <p className="text-sm font-semibold text-gray-900">Jai Indra</p>
          <p className="text-xs text-gray-500">Frontend Developer</p>
        </div>
      </div>
    </aside>
  );
}
