import React, { useState } from "react";
import { useLocation, Link } from "react-router-dom";

export default function Sidebar() {
  return (
    <aside className="w-72 bg-white border-r min-h-screen flex flex-col">
      <div className="p-6 font-bold text-xl">Project M.</div>

      <nav className="flex-1 px-4 text-gray-600">
        <ul className="space-y-2">
          {['Home', 'Messages', 'Tasks', 'Members', 'Settings'].map((item) => (
            <li key={item} className="px-3 py-2 rounded-md hover:bg-gray-100 cursor-pointer flex items-center gap-3">
              <span className="w-3 h-3 rounded-full bg-indigo-100" />
              <span>{item}</span>
            </li>
          ))}
        </ul>

        <h4 className="mt-6 text-xs uppercase text-gray-400">My Projects</h4>
        <ul className="mt-2 space-y-2">
          {['Mobile App', 'Website Redesign', 'Design System', 'Wireframes'].map((proj, i) => (
            <li key={proj} className={`px-3 py-2 rounded-md cursor-pointer flex items-center justify-between ${i === 0 ? 'bg-indigo-50 text-indigo-600 font-medium' : 'hover:bg-gray-100'}`}>
              <div className="flex items-center gap-3">
                <span className={`w-2 h-2 rounded-full ${i === 0 ? 'bg-indigo-500' : 'bg-gray-300'}`} />
                <span>{proj}</span>
              </div>
              {i === 0 && <span className="text-xs text-gray-400">...</span>}
            </li>
          ))}
        </ul>
      </nav>

      <div className="m-4 bg-indigo-50 rounded-xl p-4 text-sm">
        <h4 className="font-medium mb-2">Thoughts Time</h4>
        <p className="text-gray-500 text-xs mb-2">We donâ€™t have any notice for you, till then you can share your thoughts.</p>
        <button className="w-full py-2 bg-white border rounded-md text-indigo-600">Write a message</button>
      </div>
    </aside>
  );
}
