import React from "react";
import Column from "./Column";
import { useSelector, useDispatch } from "react-redux";
import { DragDropContext } from "react-beautiful-dnd";
import { updateTask } from "../redux/slices/tasksSlice";

const statusOrder = [
  { id: "todo", title: "To Do" },
  { id: "inprogress", title: "On Progress" },
  { id: "done", title: "Done" },
];

const Board = ({ filters }) => {
  const tasksObj = useSelector((state) => state.tasks.tasks);
  const dispatch = useDispatch();

  const tasksList = Object.values(tasksObj || {});

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const { draggableId, destination } = result;
    const destColumn = destination.droppableId;
    dispatch(updateTask({ id: draggableId, changes: { status: destColumn } }));
  };

  const applyFilters = (items) => {
    return items.filter((t) => {
      if (!t) return false;
      if (
        filters?.category &&
        filters.category !== "All" &&
        t.category !== filters.category
      )
        return false;
      if (
        filters?.priority &&
        filters.priority !== "All" &&
        t.priority !== filters.priority
      )
        return false;
      if (
        filters?.search &&
        !t.title.toLowerCase().includes(filters.search.toLowerCase())
      )
        return false;
      return true;
    });
  };

  return (
    <div className="flex-1 p-6">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <h1 className="text-2xl font-bold text-gray-900">Mobile App</h1>
        <div className="flex items-center space-x-3">
          {/* Filter + Today dropdowns */}
          <button className="flex items-center px-3 py-1.5 text-sm border rounded-lg text-gray-600">
            <span className="mr-1">Filter</span>
            <svg
              className="w-4 h-4"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path d="M19 9l-7 7-7-7" />
            </svg>
          </button>
          <button className="flex items-center px-3 py-1.5 text-sm border rounded-lg text-gray-600">
            <span className="mr-1">Today</span>
            <svg
              className="w-4 h-4"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path d="M19 9l-7 7-7-7" />
            </svg>
          </button>

          {/* Invite avatars */}
          <div className="flex -space-x-2">
            <img
              src="/avatars/a1.png"
              alt="user"
              className="w-8 h-8 rounded-full border-2 border-white"
            />
            <img
              src="/avatars/a2.png"
              alt="user"
              className="w-8 h-8 rounded-full border-2 border-white"
            />
            <img
              src="/avatars/a3.png"
              alt="user"
              className="w-8 h-8 rounded-full border-2 border-white"
            />
            <div className="w-8 h-8 rounded-full bg-purple-100 flex items-center justify-center text-sm font-medium text-purple-600 border-2 border-white">
              +2
            </div>
          </div>

          <button className="px-4 py-1.5 bg-purple-600 text-white rounded-lg">
            Invite
          </button>
          <button className="px-4 py-1.5 border rounded-lg text-gray-600">
            Share
          </button>
        </div>
      </div>

      {/* Columns with DnD */}
      <DragDropContext onDragEnd={handleDragEnd}>
        <div className="grid grid-cols-3 gap-6">
          {statusOrder.map((col) => (
            <Column
              key={col.id}
              column={col}
              tasks={applyFilters(tasksList.filter((t) => t.status === col.id))}
            />
          ))}
        </div>
      </DragDropContext>
    </div>
  );
};

export default Board;
