import React from 'react';
import { Draggable } from 'react-beautiful-dnd';

export default function TaskCard({ task, index }) {
  const priorityClass = task.priority === 'High' ? 'bg-red-100 text-red-700' : task.priority === 'Medium' ? 'bg-yellow-100 text-yellow-700' : 'bg-green-100 text-green-700';
  return (
    <Draggable draggableId={task.id} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          className="bg-white p-4 rounded-md shadow-sm border"
        >
          <div className="flex justify-between items-start">
            <h4 className="font-semibold">{task.title}</h4>
            <span className={`text-xs px-2 py-1 rounded ${priorityClass}`}>{task.priority}</span>
          </div>
          <p className="text-sm text-gray-600 mt-2">{task.description}</p>
          <div className="flex items-center justify-between mt-3 text-xs text-gray-500">
            <div>{task.category}</div>
            <div>{task.dueDate ? new Date(task.dueDate).toLocaleDateString() : ''}</div>
          </div>
          <div className="bg-white rounded-xl shadow-sm p-4 mb-4">
  <div className="flex items-center justify-between mb-2">
    <h3 className="font-semibold text-gray-900">{task.title}</h3>
    <span className={`px-2 py-0.5 rounded-full text-xs font-medium ${
      task.priority === "High" ? "bg-red-100 text-red-600" :
      task.priority === "Completed" ? "bg-green-100 text-green-600" :
      "bg-yellow-100 text-yellow-600"
    }`}>
      {task.priority}
    </span>
  </div>
  <p className="text-sm text-gray-600 mb-3">{task.description}</p>
  <div className="flex items-center justify-between text-xs text-gray-400">
    <div className="flex -space-x-2">
      {task.assignees.map((user, i) => (
        <img key={i} src={user.avatar} alt="" className="w-6 h-6 rounded-full border-2 border-white" />
      ))}
    </div>
    <div className="flex space-x-3">
      <span>{task.comments} comments</span>
      <span>{task.files} files</span>
    </div>
  </div>
</div>

        </div>

      )}
    </Draggable>
  );
}
