import React from 'react';
import { Draggable } from 'react-beautiful-dnd';

export default function TaskCard({ task, index }) {
  const priorityClass = task.priority === 'High' ? 'bg-red-100 text-red-700' : task.priority === 'Medium' ? 'bg-yellow-100 text-yellow-700' : 'bg-green-100 text-green-700';
  return (
    <Draggable draggableId={task.id} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          className="bg-white p-4 rounded-md shadow-sm border"
        >
          <div className="flex justify-between items-start">
            <h4 className="font-semibold">{task.title}</h4>
            <span className={`text-xs px-2 py-1 rounded ${priorityClass}`}>{task.priority}</span>
          </div>
          <p className="text-sm text-gray-600 mt-2">{task.description}</p>
          <div className="flex items-center justify-between mt-3 text-xs text-gray-500">
            <div>{task.category}</div>
            <div>{task.dueDate ? new Date(task.dueDate).toLocaleDateString() : ''}</div>
          </div>

        </div>

      )}
    </Draggable>
  );
}
