import React from 'react';
import TaskCard from './TaskCard';
import { Droppable } from 'react-beautiful-dnd';

export default function Column({ column, tasks }) {
  return (
    <div className="bg-white rounded-lg p-4 min-h-[300px] card-shadow">
      <div className="flex items-center justify-between mb-4">
        <h3 className="font-semibold">{column.title} <span className="text-sm text-gray-400">({tasks.length})</span></h3>
        <button className="text-gray-400">...</button>
      </div>

      <Droppable droppableId={column.id}>
        {(provided) => (
          <div ref={provided.innerRef} {...provided.droppableProps} className="space-y-4 min-h-[200px]">
            {tasks.map((task, idx) => (
              <TaskCard key={task.id} task={task} index={idx} />
            ))}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </div>
  );
}
